{"filter":false,"title":"count_sec.rb","tooltip":"/ruby/count_sec/count_sec.rb","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":35,"column":3},"action":"insert","lines":["#時間測るメソッド","def measuretime","  start = Time.now","  gets","  stop = Time.now","  elapsed_time = stop - start","end","#判定するメソッド","def judgment(elapsed_time)","  sec = elapsed_time.floor","  elapsed_time = elapsed_time.round(3)","  puts \"#{elapsed_time}秒!!!！\"","  if sec === 5","    return true","  end","  false","end","while true do","  puts \"5秒当てゲーム\\nエンターキーを一回押して心の中で5秒数えて５秒たったらもう一度エンターキーを押してください。\"","  gets","  puts '５秒後にもう一度エンターキーを押してください。'","  if judgment(measuretime)","    puts \"成功です！\"","  else","    puts \"失敗です！\"","  end","  puts 'もう一度続ける？(y/n)'","  input = gets.chomp","  if input == 'y'","    true","  elsif input == 'n'","    break","  else","    puts 'ちゃんと入力してくれなかったのでもう一回スタート'","  end","end"],"id":1}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":35,"column":3},"end":{"row":35,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1576571336436,"hash":"3b9c8313121c3bddad9cfa0da6c2b75e5df703d6"}